import { Component } from '@angular/core';

import {
  trigger,
  state,
  style,
  transition,
  animate,
} from '@angular/animations';

import {
  AccordionsItem,
  ISkillsItem,
  IProjectItem,
  IJobItem,
} from '../../base-interface';

